import React from 'react';

const ServicesSection = () => {
  const services = [
    { id: 1, title: "Hair Styling", icon: "✂️" },
    { id: 2, title: "Nail Care", icon: "💅" },
    { id: 3, title: "Makeup", icon: "💄" },
    { id: 4, title: "Facials", icon: "✨" },
    { id: 5, title: "Microblading", icon: "🎨" },
    { id: 6, title: "Bridal", icon: "👰" }
  ];

  const wigs = [
    {
      id: 1,
      name: "Silky Straight",
      price: "$89",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Premium human hair"
    },
    {
      id: 2,
      name: "Curly Waves",
      price: "$95",
      image: "https://images.unsplash.com/photo-1595476108010-b4d1f102b1b3?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Natural curl pattern"
    },
    {
      id: 3,
      name: "Blonde Highlights",
      price: "$120",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Sun-kissed blonde"
    },
    {
      id: 4,
      name: "Black Elegance",
      price: "$75",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Classic black beauty"
    },
    {
      id: 5,
      name: "Red Velvet",
      price: "$110",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Bold red statement"
    },
    {
      id: 6,
      name: "Ombre Blend",
      price: "$135",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Gradient perfection"
    }
  ];

  return (
    <section className="py-12 bg-gradient-to-br from-cream-50 to-pearl-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-8">
          <h2 className="font-playfair text-2xl md:text-3xl font-light text-cream-700 mb-2">
            Our Services
          </h2>
          <div className="w-12 h-0.5 bg-blush-300 mx-auto"></div>
        </div>

        {/* Split Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-start">
          {/* Left Side - Services Grid */}
          <div className="space-y-4">
            {services.map((service, index) => (
              <div 
                key={service.id}
                className="group bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl transition-all duration-500 transform hover:-translate-y-1 overflow-hidden border border-cream-200/50 cursor-pointer"
                style={{ animationDelay: `${index * 0.1}s` }}
                onMouseEnter={() => setHoveredService(service)}
                onMouseLeave={() => setHoveredService(null)}
              >
                <div className="p-4">
                  <div className="flex items-center space-x-3">
                    <div className="text-2xl bg-blush-200/80 rounded-full w-10 h-10 flex items-center justify-center border border-cream-200/50">
                      {service.icon}
                    </div>
                    <div className="flex-1">
                      <h3 className="font-playfair text-lg font-light text-cream-700 mb-1">
                        {service.title}
                      </h3>
                      <p className="font-poppins text-cream-500 text-xs font-light">
                        {service.description}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Right Side - Interactive Image Display */}
          <div className="lg:sticky lg:top-8">
            <div className="relative h-[400px] rounded-2xl overflow-hidden shadow-xl border border-cream-200/50">
              {hoveredService ? (
                <div className="relative h-full">
                  {/* Slideshow Container */}
                  <div className="absolute inset-0">
                    {hoveredService.images.map((image, index) => (
                      <div
                        key={index}
                        className={`absolute inset-0 transition-opacity duration-1000 ${
                          index === 0 ? 'opacity-100' : 'opacity-0'
                        }`}
                        style={{
                          animationDelay: `${index * 2}s`,
                          animation: `fadeInOut 8s infinite ${index * 2}s`
                        }}
                      >
                        <img 
                          src={image} 
                          alt={`${hoveredService.title} ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                        <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
                      </div>
                    ))}
                  </div>
                  
                  {/* Service Info Overlay */}
                  <div className="absolute bottom-0 left-0 right-0 p-4 text-white">
                    <h3 className="font-playfair text-lg font-light mb-1">
                      {hoveredService.title}
                    </h3>
                    <p className="font-poppins text-xs font-light opacity-90">
                      {hoveredService.description}
                    </p>
                  </div>

                  {/* Slideshow Indicator */}
                  <div className="absolute top-4 right-4 bg-white/20 backdrop-blur-sm rounded-full px-3 py-1">
                    <span className="text-white text-xs font-poppins">Gallery</span>
                  </div>
                </div>
              ) : (
                <div className="h-full bg-gradient-to-br from-cream-200 to-pearl-300 flex items-center justify-center">
                  <div className="text-center">
                    <div className="w-16 h-16 bg-blush-200/80 rounded-full flex items-center justify-center mx-auto mb-4">
                      <span className="text-2xl">✨</span>
                    </div>
                    <h3 className="font-playfair text-lg font-light text-cream-700 mb-2">
                      Hover over a service
                    </h3>
                    <p className="font-poppins text-cream-600 font-light text-sm">
                      to see our gallery
                    </p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center mt-8">
          <button 
            onClick={() => document.getElementById('appointment').scrollIntoView({ behavior: 'smooth' })}
            className="bg-blush-400/80 hover:bg-blush-500/90 text-cream-50 font-poppins font-light px-6 py-2 rounded-full text-sm transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl backdrop-blur-sm"
          >
            Book Your Service
          </button>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
