import React from 'react';
import { motion } from 'framer-motion';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const HeroSection = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
    fade: true,
    cssEase: "linear",
    pauseOnHover: true,
  };

  const heroImages = [
    {
      id: 1,
      url: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1974&q=80",
      alt: "Elegant hair styling"
    },
    {
      id: 2,
      url: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1969&q=80",
      alt: "Professional makeup application"
    },
    {
      id: 3,
      url: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
      alt: "Luxury nail care"
    },
    {
      id: 4,
      url: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80",
      alt: "Relaxing facial treatment"
    }
  ];

  return (
    <section className="relative h-screen overflow-hidden">
      <Slider {...settings}>
        {heroImages.map((image) => (
          <div key={image.id} className="relative h-screen">
            <div 
              className="h-full w-full bg-cover bg-center bg-no-repeat"
              style={{ backgroundImage: `url(${image.url})` }}
            >
              {/* Luxury Gradient Overlay */}
              <div className="absolute inset-0 bg-gradient-to-br from-charcoal-900/40 via-gold-500/20 to-blush-400/30"></div>
              <div className="absolute inset-0 bg-gradient-to-t from-charcoal-900/60 via-transparent to-transparent"></div>
              
              {/* Floating Elements */}
              <div className="absolute top-20 left-10 w-20 h-20 border border-gold-300/30 rounded-full animate-luxury-float"></div>
              <div className="absolute top-40 right-16 w-12 h-12 border border-blush-300/40 rounded-full animate-luxury-float" style={{ animationDelay: '1s' }}></div>
              <div className="absolute bottom-40 left-20 w-16 h-16 border border-cream-300/50 rounded-full animate-luxury-float" style={{ animationDelay: '2s' }}></div>
              
              {/* Content */}
              <div className="absolute inset-0 flex items-center justify-center">
                <motion.div 
                  className="text-center text-white px-4 max-w-5xl mx-auto"
                  initial={{ opacity: 0, y: 50 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 1, delay: 0.5 }}
                >
                  <motion.h1 
                    className="text-luxury-heading text-cream-50 drop-shadow-2xl mb-6"
                    initial={{ opacity: 0, scale: 0.8 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ duration: 1, delay: 0.8 }}
                  >
                    Prissy Salon
                  </motion.h1>
                  <motion.h2 
                    className="text-luxury-subheading text-cream-100 drop-shadow-lg mb-4"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 1.2 }}
                  >
                    Hair & Beauty
                  </motion.h2>
                  <motion.p 
                    className="text-luxury-body text-cream-200 drop-shadow-md mb-8"
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 1.5 }}
                  >
                    Redefining Beauty & Elegance
                  </motion.p>
                  <motion.div 
                    className="mt-8"
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, delay: 1.8 }}
                  >
                    <motion.button 
                      onClick={() => document.getElementById('appointment')?.scrollIntoView({ behavior: 'smooth' })}
                      className="btn-luxury group relative overflow-hidden"
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      <span className="relative z-10">Book Your Appointment</span>
                      <div className="absolute inset-0 bg-gradient-to-r from-gold-400 to-gold-500 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    </motion.button>
                  </motion.div>
                </motion.div>
              </div>
            </div>
          </div>
        ))}
      </Slider>
      
      {/* Luxury Scroll Indicator */}
      <motion.div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, delay: 2.5 }}
      >
        <div className="w-6 h-10 border-2 border-gold-300/60 rounded-full flex justify-center group cursor-pointer">
          <motion.div 
            className="w-1 h-3 bg-gold-300 rounded-full mt-2"
            animate={{ y: [0, 8, 0] }}
            transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          ></motion.div>
        </div>
        <p className="text-gold-300/80 text-xs mt-2 font-inter font-light tracking-wider">SCROLL</p>
      </motion.div>
    </section>
  );
};

export default HeroSection;
