import React from 'react';

const ServicesSection = () => {
  const handleWigPurchase = (wig) => {
    // Placeholder phone number - replace with actual salon phone number
    const phoneNumber = "1234567890"; // Replace with actual phone number
    const message = `Hi! I'm interested in buying the ${wig.name} wig (${wig.price}). Could you please provide more details about availability and how to purchase?`;
    const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };
  const services = [
    { 
      id: 1, 
      title: "Hair Styling", 
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    },
    { 
      id: 2, 
      title: "Nail Care", 
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    },
    { 
      id: 3, 
      title: "Makeup", 
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    },
    { 
      id: 4, 
      title: "Facials", 
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    },
    { 
      id: 5, 
      title: "Microblading", 
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    },
    { 
      id: 6, 
      title: "Bridal", 
      image: "https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80"
    }
  ];

  const wigs = [
    {
      id: 1,
      name: "Silky Straight",
      price: "$89",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Premium human hair"
    },
    {
      id: 2,
      name: "Curly Waves",
      price: "$95",
      image: "https://images.unsplash.com/photo-1595476108010-b4d1f102b1b3?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Natural curl pattern"
    },
    {
      id: 3,
      name: "Blonde Highlights",
      price: "$120",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Sun-kissed blonde"
    },
    {
      id: 4,
      name: "Black Elegance",
      price: "$75",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Classic black beauty"
    },
    {
      id: 5,
      name: "Red Velvet",
      price: "$110",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Bold red statement"
    },
    {
      id: 6,
      name: "Ombre Blend",
      price: "$135",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Gradient perfection"
    },
    {
      id: 7,
      name: "Chestnut Brown",
      price: "$98",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Rich brown tones"
    },
    {
      id: 8,
      name: "Platinum Blonde",
      price: "$145",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Stunning platinum"
    },
    {
      id: 9,
      name: "Auburn Waves",
      price: "$105",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Warm auburn curls"
    },
    {
      id: 10,
      name: "Jet Black",
      price: "$85",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Deep black shine"
    }
  ];

  return (
    <section className="py-16 bg-gradient-to-br from-gray-100 to-gray-200 relative overflow-hidden">
      {/* Background Inscription with Low Opacity */}
      <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
        <div className="text-9xl md:text-[12rem] lg:text-[16rem] font-playfair font-bold text-gray-300 opacity-10 select-none">
          BEAUTY
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="font-playfair text-4xl md:text-5xl font-bold text-gray-800 mb-4">
            VISIT US FOR THE PERFECT BALANCE OF BEAUTY & ELEGANCE
          </h2>
          <div className="w-24 h-1 bg-red-500 mx-auto"></div>
        </div>

        {/* Services Grid - Slide Style */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {services.map((service, index) => (
            <div 
              key={service.id}
              className="group relative bg-white rounded-2xl shadow-xl overflow-hidden hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-2"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              {/* Service Image */}
              <div className="relative h-64 overflow-hidden">
                <img 
                  src={service.image}
                  alt={service.title}
                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent"></div>
                
                {/* READ MORE Button */}
                <div className="absolute bottom-4 right-4">
                  <button className="bg-red-500 hover:bg-red-600 text-white font-poppins font-bold py-2 px-4 rounded text-sm transition-all duration-300 transform hover:scale-105 shadow-lg">
                    READ MORE
                  </button>
                </div>
              </div>

              {/* Service Title */}
              <div className="p-6">
                <h3 className="font-playfair text-xl font-bold text-gray-600 uppercase tracking-wide">
                  â€” {service.title}
                </h3>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Wig Collection Section */}
      <div className="mb-12">
        <div className="text-center mb-8">
          <h3 className="font-playfair text-2xl md:text-3xl font-bold text-cream-800 mb-2">
            Premium Wig Collection
          </h3>
          <p className="font-poppins text-cream-600 font-light">
            High-quality wigs for every style
          </p>
        </div>

        {/* Wig Slider - Scrollable with Tuck Effect */}
        <div className="relative">
          {/* Gradient Fade Edges */}
          <div className="absolute left-0 top-0 bottom-0 w-8 bg-gradient-to-r from-cream-50 to-transparent z-10 pointer-events-none"></div>
          <div className="absolute right-0 top-0 bottom-0 w-8 bg-gradient-to-l from-cream-50 to-transparent z-10 pointer-events-none"></div>
          
          <div className="flex gap-4 overflow-x-auto scrollbar-hide pb-4 scroll-smooth" style={{ scrollBehavior: 'smooth' }}>
            {wigs.map((wig, index) => (
              <div 
                key={wig.id}
                className="group flex-shrink-0 w-72 bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-2 hover:scale-105 overflow-hidden border border-cream-200/50"
                style={{ 
                  animationDelay: `${index * 0.05}s`,
                  transform: 'translateZ(0)',
                  backfaceVisibility: 'hidden'
                }}
              >
                {/* Wig Image */}
                <div className="relative h-48 overflow-hidden">
                  <img 
                    src={wig.image} 
                    alt={wig.name}
                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>
                  
                  {/* Price Tag */}
                  <div className="absolute top-3 right-3 bg-blush-400/90 backdrop-blur-sm rounded-full px-3 py-1 group-hover:bg-blush-500 transition-colors duration-300">
                    <span className="text-white font-poppins font-bold text-sm group-hover:scale-110 transition-transform duration-300">
                      {wig.price}
                    </span>
                  </div>
                </div>

                {/* Wig Info */}
                <div className="p-4">
                  <h4 className="font-playfair text-lg font-bold text-cream-800 mb-1 group-hover:text-blush-500 transition-colors duration-300">
                    {wig.name}
                  </h4>
                  <p className="font-poppins text-cream-600 text-xs font-light mb-3">
                    {wig.description}
                  </p>
                  
                  {/* Buy Me Button */}
                  <button 
                    onClick={() => handleWigPurchase(wig)}
                    className="w-full bg-green-500 hover:bg-green-600 text-white font-poppins font-bold py-2 px-3 rounded-full text-xs transition-all duration-300 transform hover:scale-105 opacity-0 group-hover:opacity-100 flex items-center justify-center space-x-2"
                  >
                    <span>ðŸ’¬</span>
                    <span>Buy Me</span>
                  </button>
                </div>

                {/* Tiny Demarcation Line */}
                {index < wigs.length - 1 && (
                  <div className="absolute -right-2 top-1/2 transform -translate-y-1/2 w-1 h-16 bg-cream-300 rounded-full"></div>
                )}
              </div>
            ))}
          </div>
          
          {/* Scroll Indicators */}
          <div className="flex justify-center mt-4 space-x-1">
            {Array.from({ length: Math.ceil(wigs.length / 4) }, (_, index) => (
              <div key={index} className="w-2 h-2 bg-blush-300 rounded-full"></div>
            ))}
          </div>
        </div>
      </div>

      {/* Call to Action */}
      <div className="text-center">
        <button 
          onClick={() => document.getElementById('appointment').scrollIntoView({ behavior: 'smooth' })}
          className="bg-blush-400/80 hover:bg-blush-500/90 text-cream-50 font-poppins font-bold px-8 py-3 rounded-full text-base transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl backdrop-blur-sm"
        >
          Book Your Service
        </button>
      </div>
    </section>
  );
};

export default ServicesSection;
