import React from 'react';
import { motion } from 'framer-motion';

const Footer = () => {
  const currentYear = new Date().getFullYear();

  return (
    <footer className="bg-gradient-to-br from-charcoal-900 via-charcoal-800 to-charcoal-900 text-cream-50 relative overflow-hidden">
      {/* Luxury Background Elements */}
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_20%_20%,rgba(209,159,82,0.1),transparent_50%)]"></div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_80%_80%,rgba(252,165,165,0.05),transparent_50%)]"></div>
      
      {/* Floating Decorative Elements */}
      <div className="absolute top-10 left-10 w-16 h-16 border border-gold-300/20 rounded-full animate-luxury-float"></div>
      <div className="absolute bottom-10 right-10 w-20 h-20 border border-blush-300/20 rounded-full animate-luxury-float" style={{ animationDelay: '2s' }}></div>
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 sm:py-16 relative">
        {/* Main Footer Content */}
        <motion.div 
          className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-8 lg:gap-12 items-start"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          {/* Salon Name */}
          <motion.div 
            className="lg:col-span-1 text-center sm:text-left"
            initial={{ opacity: 0, x: -30 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <motion.h3 
              className="text-luxury-heading text-cream-50 mb-4"
              whileHover={{ color: "#d19f52" }}
              transition={{ duration: 0.3 }}
            >
              Prissy Salon
            </motion.h3>
            <p className="text-luxury-body text-cream-200 font-light">
              Redefining Beauty & Elegance
            </p>
            <div className="mt-4 w-16 h-0.5 bg-gradient-to-r from-gold-400 to-gold-500"></div>
          </motion.div>

          {/* Contact Information */}
          <motion.div 
            className="space-y-4 text-center sm:text-left"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <h4 className="text-luxury-subheading text-cream-100 mb-4">Contact Us</h4>
            <motion.div 
              className="flex items-center justify-center sm:justify-start space-x-3 group cursor-pointer"
              whileHover={{ x: 5 }}
              transition={{ duration: 0.3 }}
            >
              <span className="text-gold-400 text-lg">üìç</span>
              <p className="text-luxury-body text-cream-200 group-hover:text-gold-300 transition-colors duration-300">
                Kiki Plaza, Badagry Roundabout
              </p>
            </motion.div>
            <motion.div 
              className="flex items-center justify-center sm:justify-start space-x-3 group cursor-pointer"
              whileHover={{ x: 5 }}
              transition={{ duration: 0.3 }}
            >
              <span className="text-gold-400 text-lg">üìû</span>
              <p className="text-luxury-body text-cream-200 group-hover:text-gold-300 transition-colors duration-300">
                (555) 123-4567
              </p>
            </motion.div>
            <motion.div 
              className="flex items-center justify-center sm:justify-start space-x-3 group cursor-pointer"
              whileHover={{ x: 5 }}
              transition={{ duration: 0.3 }}
            >
              <span className="text-gold-400 text-lg">‚úâÔ∏è</span>
              <p className="text-luxury-body text-cream-200 group-hover:text-gold-300 transition-colors duration-300">
                info@prissysalon.com
              </p>
            </motion.div>
          </motion.div>

          {/* Business Hours */}
          <motion.div 
            className="space-y-4 text-center sm:text-left"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, delay: 0.6 }}
            viewport={{ once: true }}
          >
            <h4 className="text-luxury-subheading text-cream-100 mb-4">Hours</h4>
            <div className="space-y-2">
              <motion.p 
                className="text-luxury-body text-cream-200"
                whileHover={{ color: "#d19f52" }}
                transition={{ duration: 0.3 }}
              >
                Mon-Fri: 9:00 AM - 7:00 PM
              </motion.p>
              <motion.p 
                className="text-luxury-body text-cream-200"
                whileHover={{ color: "#d19f52" }}
                transition={{ duration: 0.3 }}
              >
                Saturday: 8:00 AM - 6:00 PM
              </motion.p>
              <motion.p 
                className="text-luxury-body text-cream-200"
                whileHover={{ color: "#d19f52" }}
                transition={{ duration: 0.3 }}
              >
                Sunday: 10:00 AM - 4:00 PM
              </motion.p>
            </div>
          </motion.div>

          {/* Social Media */}
          <motion.div 
