import React from 'react';
import { motion } from 'framer-motion';

const AppointmentSection = () => {
  return (
    <section id="appointment" className="py-16 sm:py-20 lg:py-24 bg-gradient-to-br from-charcoal-50 via-cream-50 to-beige-50 relative overflow-hidden">
      {/* Luxury Background Elements */}
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_30%_20%,rgba(209,159,82,0.06),transparent_50%)]"></div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_70%_80%,rgba(252,165,165,0.04),transparent_50%)]"></div>
      
      {/* Floating Decorative Elements */}
      <div className="absolute top-20 left-10 w-24 h-24 border border-gold-200/20 rounded-full animate-luxury-float"></div>
      <div className="absolute bottom-20 right-10 w-32 h-32 border border-blush-200/30 rounded-full animate-luxury-float" style={{ animationDelay: '2s' }}></div>
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
        <motion.div 
          className="text-center"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          {/* Section Header */}
          <motion.div 
            className="mb-12 sm:mb-16"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="inline-block mb-6">
              <div className="w-20 h-0.5 bg-gradient-to-r from-transparent via-gold-400 to-transparent mx-auto"></div>
            </div>
            <h2 className="text-luxury-heading text-charcoal-800 mb-6">
              Connect With <span className="font-bold text-gold-600">Us</span>
            </h2>
            <p className="text-luxury-body text-charcoal-600 max-w-3xl mx-auto px-4">
              Follow our journey and visit our luxurious salon for an unforgettable beauty experience
            </p>
          </motion.div>

          {/* Split Layout - Social Media Left, Map Right */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 lg:gap-16">
            {/* Left Side - Social Media Links */}
            <motion.div 
              className="flex items-center justify-center order-2 lg:order-1"
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              viewport={{ once: true }}
            >
              <div className="text-center">
                {/* Subheading */}
                <motion.h3 
                  className="text-luxury-subheading text-charcoal-800 mb-8"
                  whileHover={{ color: "#d19f52" }}
                  transition={{ duration: 0.3 }}
                >
                  Follow Our Journey
                </motion.h3>
                
                {/* Social Media Links */}
                <div className="flex flex-col sm:flex-row items-center justify-center space-y-6 sm:space-y-0 sm:space-x-8 lg:space-x-12">
                  <motion.div 
                    className="group"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <button className="btn-luxury-secondary group">
                      <span className="relative z-10 flex items-center space-x-3">
                        <span className="text-2xl">ðŸ“˜</span>
                        <span>Facebook</span>
                        <span className="text-lg group-hover:translate-x-1 transition-transform duration-300">â†’</span>
                      </span>
                    </button>
                  </motion.div>
                  
                  <motion.div 
                    className="group"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <button className="btn-luxury-secondary group">
                      <span className="relative z-10 flex items-center space-x-3">
                        <span className="text-2xl">ðŸ“·</span>
                        <span>Instagram</span>
                        <span className="text-lg group-hover:translate-x-1 transition-transform duration-300">â†’</span>
                      </span>
                    </button>
                  </motion.div>
                  
                  <motion.div 
                    className="group"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <button className="btn-luxury-secondary group">
                      <span className="relative z-10 flex items-center space-x-3">
                        <span className="text-2xl">ðŸŽµ</span>
                        <span>TikTok</span>
                        <span className="text-lg group-hover:translate-x-1 transition-transform duration-300">â†’</span>
                      </span>
                    </button>
                  </motion.div>
                </div>
              </div>
            </motion.div>

            {/* Right Side - Map Section */}
            <motion.div 
              className="flex flex-col order-1 lg:order-2"
              initial={{ opacity: 0, x: 50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8, delay: 0.6 }}
              viewport={{ once: true }}
            >
              <motion.div 
                className="text-center mb-6"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 0.8 }}
                viewport={{ once: true }}
              >
                <h3 className="text-luxury-subheading text-charcoal-800 mb-4">
                  Visit Our Salon
                </h3>
                <p className="text-luxury-body text-charcoal-600">
                  Experience luxury in the heart of Lagos
                </p>
              </motion.div>
              
              {/* Google Map */}
              <motion.div 
                className="relative rounded-3xl overflow-hidden h-56 sm:h-64 lg:h-72 shadow-2xl border border-gold-200/20"
                whileHover={{ scale: 1.02 }}
                transition={{ duration: 0.3 }}
              >
                <iframe
                  src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3963.4!2d3.4!3d6.4!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x103b8f8f8f8f8f8f%3A0x1234567890123456!2sBadagry%20Roundabout%2C%20Lagos%20State%2C%20Nigeria!5e0!3m2!1sen!2sng!4v1631234567890!5m2!1sen!2sng"
                  width="100%"
                  height="100%"
                  style={{ border: 0 }}
                  allowFullScreen={true}
                  loading="lazy"
                  referrerPolicy="no-referrer-when-downgrade"
                  title="Kiki Plaza, Badagry Roundabout, Lagos State"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-charcoal-900/20 to-transparent pointer-events-none"></div>
              </motion.div>
              
              {/* Address Info */}
              <motion.div 
                className="text-center mt-8"
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: 1 }}
                viewport={{ once: true }}
              >
                <motion.p 
                  className="text-luxury-body text-charcoal-700 font-medium mb-2"
                  whileHover={{ color: "#d19f52" }}
                  transition={{ duration: 0.3 }}
                >
                  Kiki Plaza, Badagry Roundabout
                </motion.p>
                <p className="text-luxury-body text-charcoal-600 text-sm mb-6">
                  Lagos State, Nigeria
                </p>
                <motion.a 
                  href="https://www.google.com/maps/search/Kiki+Plaza+Badagry+Roundabout+Lagos+State"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn-luxury inline-block"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Get Directions
                </motion.a>
              </motion.div>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default AppointmentSection;
