import React from 'react';

const WigsSaleSection = () => {
  const wigs = [
    {
      id: 1,
      name: "Silky Straight Wig",
      price: "â‚¦15,000",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Premium quality straight wig"
    },
    {
      id: 2,
      name: "Curly Bob Wig",
      price: "â‚¦18,000",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Beautiful curly bob style"
    },
    {
      id: 3,
      name: "Long Wavy Wig",
      price: "â‚¦22,000",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Elegant long wavy hair"
    },
    {
      id: 4,
      name: "Short Pixie Wig",
      price: "â‚¦12,000",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Chic short pixie cut"
    },
    {
      id: 5,
      name: "Blonde Highlights Wig",
      price: "â‚¦20,000",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Stylish blonde highlights"
    }
  ];

  const handleWhatsAppClick = (wig) => {
    const message = `Hi! I'm interested in buying the ${wig.name} for ${wig.price}. Is it available?`;
    const whatsappUrl = `https://wa.me/2348123456789?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <section id="wigs-sale" className="py-12 sm:py-16 lg:py-20 bg-gradient-to-br from-rose-50 to-pink-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-8 sm:mb-12 lg:mb-16">
          <h2 className="font-playfair text-3xl sm:text-4xl md:text-5xl font-bold text-gray-800 mb-3 sm:mb-4">
            Wigs for Sale
          </h2>
          <p className="font-poppins text-gray-600 text-base sm:text-lg max-w-2xl mx-auto px-4">
            Premium quality wigs available for purchase. Contact us via WhatsApp to buy!
          </p>
        </div>

        {/* Wigs Horizontal Scroll */}
        <div className="overflow-x-auto pb-4 scrollbar-hide">
          <div className="flex space-x-3 sm:space-x-4 lg:space-x-6 min-w-max">
            {wigs.map((wig) => (
              <div 
                key={wig.id}
                className="group relative bg-white rounded-lg sm:rounded-xl shadow-lg overflow-hidden hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-1 sm:hover:-translate-y-2 flex-shrink-0 w-48 sm:w-56 lg:w-64"
              >
                {/* Wig Image */}
                <div className="relative h-32 sm:h-40 lg:h-48 overflow-hidden">
                  <img 
                    src={wig.image}
                    alt={wig.name}
                    className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                  />
                  <div className="absolute inset-0 bg-gradient-to-t from-black/40 to-transparent"></div>
                  {/* Price Badge */}
                  <div className="absolute top-2 right-2 bg-rose-500 text-white px-2 py-1 rounded-full text-xs font-bold">
                    {wig.price}
                  </div>
                </div>

                {/* Wig Info */}
                <div className="p-3 sm:p-4">
                  <h3 className="font-playfair text-sm sm:text-base font-bold text-gray-800 uppercase tracking-wide mb-1">
                    {wig.name}
                  </h3>
                  <p className="font-poppins text-gray-600 text-xs mb-3">
                    {wig.description}
                  </p>
                  
                  {/* Buy Button */}
                  <button
                    onClick={() => handleWhatsAppClick(wig)}
                    className="w-full bg-green-500 hover:bg-green-600 text-white font-poppins font-bold py-1.5 px-3 rounded-lg transition-all duration-300 transform hover:scale-105 flex items-center justify-center space-x-1 text-sm"
                  >
                    <span>Buy Me</span>
                    <span>ðŸ’¬</span>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default WigsSaleSection;
