import React, { useState } from 'react';

const ServicesSection = () => {
  const [hoveredService, setHoveredService] = useState(null);

  const services = [
    {
      id: 1,
      title: "Hair Styling",
      description: "Professional cuts & styling",
      icon: "✂️",
      images: [
        "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
        "https://images.unsplash.com/photo-1595476108010-b4d1f102b1b3?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
      ]
    },
    {
      id: 2,
      title: "Nail Care",
      description: "Luxurious manicure & pedicure",
      icon: "💅",
      images: [
        "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
        "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
      ]
    },
    {
      id: 3,
      title: "Makeup",
      description: "Professional makeup artistry",
      icon: "💄",
      images: [
        "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
        "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
      ]
    },
    {
      id: 4,
      title: "Facials",
      description: "Rejuvenating skin treatments",
      icon: "✨",
      images: [
        "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80",
        "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80"
      ]
    }
  ];

  return (
    <section className="py-12 bg-gradient-to-br from-cream-50 to-pearl-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-8">
          <h2 className="font-playfair text-2xl md:text-3xl font-light text-cream-700 mb-2">
            Our Services
          </h2>
          <div className="w-12 h-0.5 bg-blush-300 mx-auto"></div>
        </div>

        {/* Split Layout */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 items-start">
          {/* Left Side - Services Grid */}
          <div className="space-y-6">
            {services.map((service, index) => (
              <div 
                key={service.id}
                className="group bg-white/80 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-xl transition-all duration-500 transform hover:-translate-y-1 overflow-hidden border border-cream-200/50 cursor-pointer"
                style={{ animationDelay: `${index * 0.1}s` }}
                onMouseEnter={() => setHoveredService(service)}
                onMouseLeave={() => setHoveredService(null)}
              >
                <div className="p-6">
                  <div className="flex items-center space-x-4">
                    <div className="text-3xl bg-blush-200/80 rounded-full w-12 h-12 flex items-center justify-center border border-cream-200/50">
                      {service.icon}
                    </div>
                    <div className="flex-1">
                      <h3 className="font-playfair text-xl font-light text-cream-700 mb-2">
                        {service.title}
                      </h3>
                      <p className="font-poppins text-cream-500 leading-relaxed text-sm font-light">
                        {service.description}
                      </p>
                    </div>
                  </div>
                  
                  {/* Hover Effect Indicator */}
                  <div className="mt-4 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                    <div className="flex items-center text-blush-400 text-sm font-poppins font-light">
                      <span>Hover to see gallery</span>
                      <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                      </svg>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Right Side - Interactive Image Display */}
          <div className="lg:sticky lg:top-8">
            <div className="relative h-[600px] rounded-3xl overflow-hidden shadow-2xl border border-cream-200/50">
              {hoveredService ? (
                <div className="relative h-full">
                  {/* Slideshow Container */}
                  <div className="absolute inset-0">
                    {hoveredService.images.map((image, index) => (
                      <div
                        key={index}
                        className={`absolute inset-0 transition-opacity duration-1000 ${
                          index === 0 ? 'opacity-100' : 'opacity-0'
                        }`}
                        style={{
                          animationDelay: `${index * 2}s`,
                          animation: `fadeInOut 8s infinite ${index * 2}s`
                        }}
                      >
                        <img 
                          src={image} 
                          alt={`${hoveredService.title} ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                        <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
                      </div>
                    ))}
                  </div>
                  
                  {/* Service Info Overlay */}
                  <div className="absolute bottom-0 left-0 right-0 p-8 text-white">
                    <h3 className="font-playfair text-2xl font-light mb-2">
                      {hoveredService.title}
                    </h3>
                    <p className="font-poppins text-sm font-light opacity-90">
                      {hoveredService.description}
                    </p>
                  </div>

                  {/* Slideshow Indicator */}
                  <div className="absolute top-4 right-4 bg-white/20 backdrop-blur-sm rounded-full px-3 py-1">
                    <span className="text-white text-xs font-poppins">Gallery</span>
                  </div>
                </div>
              ) : (
                <div className="h-full bg-gradient-to-br from-cream-200 to-pearl-300 flex items-center justify-center">
                  <div className="text-center">
                    <div className="w-20 h-20 bg-blush-200/80 rounded-full flex items-center justify-center mx-auto mb-6">
                      <span className="text-3xl">✨</span>
                    </div>
                    <h3 className="font-playfair text-2xl font-light text-cream-700 mb-4">
                      Hover over a service
                    </h3>
                    <p className="font-poppins text-cream-600 font-light">
                      to see our beautiful gallery
                    </p>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center mt-16">
          <p className="font-poppins text-lg text-cream-700 mb-6">
            Ready to experience luxury beauty services?
          </p>
          <button 
            onClick={() => document.getElementById('appointment').scrollIntoView({ behavior: 'smooth' })}
            className="bg-blush-400/80 hover:bg-blush-500/90 text-cream-50 font-poppins font-light px-8 py-3 rounded-full text-base transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl backdrop-blur-sm"
          >
            Book Your Service
          </button>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
