import React from 'react';
import { motion } from 'framer-motion';

const WigsSaleSection = () => {
  const wigs = [
    {
      id: 1,
      name: "Silky Straight Wig",
      price: "â‚¦15,000",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Premium quality straight wig"
    },
    {
      id: 2,
      name: "Curly Bob Wig",
      price: "â‚¦18,000",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Beautiful curly bob style"
    },
    {
      id: 3,
      name: "Long Wavy Wig",
      price: "â‚¦22,000",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Elegant long wavy hair"
    },
    {
      id: 4,
      name: "Short Pixie Wig",
      price: "â‚¦12,000",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Chic short pixie cut"
    },
    {
      id: 5,
      name: "Blonde Highlights Wig",
      price: "â‚¦20,000",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&q=80",
      description: "Stylish blonde highlights"
    }
  ];

  const handleWhatsAppClick = (wig) => {
    const message = `Hi! I'm interested in buying the ${wig.name} for ${wig.price}. Is it available?`;
    const whatsappUrl = `https://wa.me/2348123456789?text=${encodeURIComponent(message)}`;
    window.open(whatsappUrl, '_blank');
  };

  return (
    <section id="wigs-sale" className="py-16 sm:py-20 lg:py-24 bg-gradient-to-br from-blush-50 via-cream-50 to-beige-50 relative overflow-hidden">
      {/* Luxury Background Elements */}
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_20%_30%,rgba(209,159,82,0.08),transparent_50%)]"></div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_80%_70%,rgba(252,165,165,0.06),transparent_50%)]"></div>
      <div className="absolute inset-0 bg-[radial-gradient(circle_at_50%_50%,rgba(241,216,180,0.1),transparent_70%)]"></div>
      
      {/* Floating Decorative Elements */}
      <div className="absolute top-20 right-10 w-28 h-28 border border-gold-200/20 rounded-full animate-luxury-float"></div>
      <div className="absolute top-40 left-20 w-20 h-20 border border-blush-200/30 rounded-full animate-luxury-float" style={{ animationDelay: '2s' }}></div>
      <div className="absolute bottom-32 right-16 w-24 h-24 border border-beige-300/40 rounded-full animate-luxury-float" style={{ animationDelay: '4s' }}></div>
      
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative">
        {/* Section Header */}
        <motion.div 
          className="text-center mb-12 sm:mb-16 lg:mb-20"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.div 
            className="inline-block mb-6"
            initial={{ scale: 0 }}
            whileInView={{ scale: 1 }}
            transition={{ duration: 0.6, delay: 0.2 }}
            viewport={{ once: true }}
          >
            <div className="w-20 h-0.5 bg-gradient-to-r from-transparent via-gold-400 to-transparent mx-auto"></div>
          </motion.div>
          <motion.h2 
            className="text-luxury-heading text-charcoal-800 mb-6"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
          >
            Premium <span className="font-bold text-gold-600">Wigs</span>
          </motion.h2>
          <motion.p 
            className="text-luxury-body text-charcoal-600 max-w-3xl mx-auto px-4"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
            viewport={{ once: true }}
          >
            Exquisite wigs crafted with luxury materials and expert artistry
          </motion.p>
        </motion.div>

        {/* Wigs Grid */}
        <motion.div 
          className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-5 gap-8 lg:gap-10"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          viewport={{ once: true }}
        >
          {wigs.map((wig, index) => (
            <motion.div 
              key={wig.id}
              className="luxury-card group cursor-pointer"
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
              whileHover={{ y: -8, scale: 1.02 }}
            >
              {/* Wig Image */}
              <div className="relative h-48 sm:h-56 overflow-hidden rounded-t-3xl">
                <motion.img 
                  src={wig.image}
                  alt={wig.name}
                  className="w-full h-full object-cover"
                  whileHover={{ scale: 1.1 }}
                  transition={{ duration: 0.6 }}
                />
                <div className="absolute inset-0 bg-gradient-to-t from-charcoal-900/60 via-transparent to-transparent"></div>
                
                {/* Luxury Overlay */}
                <div className="absolute inset-0 bg-gradient-to-br from-gold-500/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"></div>
                
                {/* Luxury Price Badge */}
                <motion.div 
                  className="absolute top-4 right-4 bg-gradient-to-r from-gold-500 to-gold-600 text-cream-50 px-4 py-2 rounded-full text-sm font-bold shadow-xl backdrop-blur-sm border border-gold-300/30"
                  whileHover={{ scale: 1.05 }}
                >
                  {wig.price}
                </motion.div>
                
                {/* Decorative Elements */}
                <div className="absolute top-4 left-4 w-8 h-8 border-2 border-gold-300/60 rounded-full opacity-0 group-hover:opacity-100 transition-all duration-500 transform group-hover:scale-110"></div>
                <div className="absolute bottom-4 right-4 w-6 h-6 border border-cream-300/80 rounded-full opacity-0 group-hover:opacity-100 transition-all duration-700"></div>
              </div>

              {/* Wig Content */}
              <div className="p-6 sm:p-8 relative">
                <div className="absolute top-0 left-8 w-16 h-0.5 bg-gradient-to-r from-gold-400 to-gold-500 transform -translate-y-1"></div>
                <motion.h3 
                  className="text-luxury-subheading text-charcoal-800 uppercase tracking-wider leading-tight mb-3"
                  whileHover={{ color: "#d19f52" }}
                  transition={{ duration: 0.3 }}
                >
                  {wig.name}
                </motion.h3>
                <motion.p 
                  className="text-luxury-body text-charcoal-600 text-sm mb-4"
                  whileHover={{ color: "#6d6d6d" }}
                  transition={{ duration: 0.3 }}
                >
                  {wig.description}
                </motion.p>
                
                {/* Elegant Underline */}
                <motion.div 
                  className="w-12 h-0.5 bg-gradient-to-r from-gold-300 to-gold-400 mb-6"
                  whileHover={{ width: "24px" }}
                  transition={{ duration: 0.5 }}
                ></motion.div>
                
                {/* Luxury Purchase Button */}
                <motion.button
                  onClick={() => handleWhatsAppClick(wig)}
                  className="btn-luxury w-full group relative overflow-hidden"
