import React from 'react';

const ServicesSection = () => {
  const services = [
    { id: 1, title: "Hair Styling", icon: "‚úÇÔ∏è" },
    { id: 2, title: "Nail Care", icon: "üíÖ" },
    { id: 3, title: "Makeup", icon: "üíÑ" },
    { id: 4, title: "Facials", icon: "‚ú®" },
    { id: 5, title: "Microblading", icon: "üé®" },
    { id: 6, title: "Bridal", icon: "üë∞" }
  ];

  const wigs = [
    {
      id: 1,
      name: "Silky Straight",
      price: "$89",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Premium human hair"
    },
    {
      id: 2,
      name: "Curly Waves",
      price: "$95",
      image: "https://images.unsplash.com/photo-1595476108010-b4d1f102b1b3?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Natural curl pattern"
    },
    {
      id: 3,
      name: "Blonde Highlights",
      price: "$120",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Sun-kissed blonde"
    },
    {
      id: 4,
      name: "Black Elegance",
      price: "$75",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Classic black beauty"
    },
    {
      id: 5,
      name: "Red Velvet",
      price: "$110",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Bold red statement"
    },
    {
      id: 6,
      name: "Ombre Blend",
      price: "$135",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Gradient perfection"
    },
    {
      id: 7,
      name: "Chestnut Brown",
      price: "$98",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Rich brown tones"
    },
    {
      id: 8,
      name: "Platinum Blonde",
      price: "$145",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Stunning platinum"
    },
    {
      id: 9,
      name: "Auburn Waves",
      price: "$105",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Warm auburn curls"
    },
    {
      id: 10,
      name: "Jet Black",
      price: "$85",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80",
      description: "Deep black shine"
    }
  ];

  return (
    <section className="py-16 bg-gradient-to-br from-cream-50 to-pearl-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-12">
          <h2 className="font-playfair text-3xl md:text-4xl font-bold text-cream-800 mb-4">
            Our Services
          </h2>
          <div className="w-16 h-1 bg-blush-400 mx-auto"></div>
        </div>

        {/* Services List - Horizontal */}
        <div className="mb-16">
          <div className="flex flex-wrap justify-center gap-6 md:gap-8">
            {services.map((service, index) => (
              <div 
                key={service.id}
                className="group flex items-center space-x-3 bg-white/90 backdrop-blur-sm rounded-full px-6 py-4 shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1 border border-cream-200/50"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className="text-2xl group-hover:scale-110 transition-transform duration-300">
                  {service.icon}
                </div>
                <span className="font-playfair text-lg font-bold text-cream-800 group-hover:text-blush-500 transition-colors duration-300">
                  {service.title}
                </span>
              </div>
            ))}
          </div>
        </div>

        {/* Wig Collection Section */}
        <div className="mb-12">
          <div className="text-center mb-8">
            <h3 className="font-playfair text-2xl md:text-3xl font-bold text-cream-800 mb-2">
              Premium Wig Collection
            </h3>
            <p className="font-poppins text-cream-600 font-light">
              High-quality wigs for every style
            </p>
          </div>

          {/* Wig Slider - Single Row */}
          <div className="relative">
            <div className="flex gap-4 overflow-x-auto scrollbar-hide pb-4">
              {wigs.map((wig, index) => (
                <div 
                  key={wig.id}
                  className="group flex-shrink-0 w-80 bg-white/90 backdrop-blur-sm rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-2 overflow-hidden border border-cream-200/50"
                  style={{ animationDelay: `${index * 0.1}s` }}
                >
                  {/* Wig Image */}
                  <div className="relative h-48 overflow-hidden">
                    <img 
                      src={wig.image} 
                      alt={wig.name}
                      className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"></div>
                    
                    {/* Price Tag */}
                    <div className="absolute top-3 right-3 bg-blush-400/90 backdrop-blur-sm rounded-full px-3 py-1 group-hover:bg-blush-500 transition-colors duration-300">
                      <span className="text-white font-poppins font-bold text-sm group-hover:scale-110 transition-transform duration-300">
                        {wig.price}
                      </span>
                    </div>
                  </div>

                  {/* Wig Info */}
                  <div className="p-4">
                    <h4 className="font-playfair text-lg font-bold text-cream-800 mb-1 group-hover:text-blush-500 transition-colors duration-300">
                      {wig.name}
                    </h4>
                    <p className="font-poppins text-cream-600 text-xs font-light mb-3">
                      {wig.description}
                    </p>
                    
                    {/* Add to Cart Button */}
                    <button className="w-full bg-blush-300/80 hover:bg-blush-400/90 text-cream-50 font-poppins font-bold py-2 px-3 rounded-full text-xs transition-all duration-300 transform hover:scale-105 opacity-0 group-hover:opacity-100">
                      Add to Cart
                    </button>
                  </div>

                  {/* Tiny Demarcation Line */}
                  {index < wigs.length - 1 && (
                    <div className="absolute -right-2 top-1/2 transform -translate-y-1/2 w-1 h-16 bg-cream-300 rounded-full"></div>
                  )}
                </div>
              ))}
            </div>
            
            {/* Scroll Indicators */}
            <div className="flex justify-center mt-4 space-x-2">
              {wigs.map((_, index) => (
                <div key={index} className="w-2 h-2 bg-blush-300 rounded-full"></div>
              ))}
            </div>
          </div>
        </div>

        {/* Call to Action */}
        <div className="text-center">
          <button 
            onClick={() => document.getElementById('appointment').scrollIntoView({ behavior: 'smooth' })}
            className="bg-blush-400/80 hover:bg-blush-500/90 text-cream-50 font-poppins font-bold px-8 py-3 rounded-full text-base transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl backdrop-blur-sm"
          >
            Book Your Service
          </button>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
