import React from 'react';

const ServicesSection = () => {
  const services = [
    {
      id: 1,
      title: "Hair Styling",
      description: "Professional cuts, coloring, and styling to enhance your natural beauty",
      icon: "‚úÇÔ∏è",
      image: "https://images.unsplash.com/photo-1560066984-138dadb4c035?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    },
    {
      id: 2,
      title: "Pedicure & Manicure",
      description: "Luxurious nail care treatments for hands and feet",
      icon: "üíÖ",
      image: "https://images.unsplash.com/photo-1516975080664-ed2fc6a32937?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    },
    {
      id: 3,
      title: "Microblading Tattoos",
      description: "Semi-permanent eyebrow enhancement for perfect arches",
      icon: "üé®",
      image: "https://images.unsplash.com/photo-1594736797933-d0401ba2fe65?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    },
    {
      id: 4,
      title: "Makeup Artistry",
      description: "Professional makeup application for any occasion",
      icon: "üíÑ",
      image: "https://images.unsplash.com/photo-1522337360788-8b13dee7a37e?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    },
    {
      id: 5,
      title: "Facials",
      description: "Rejuvenating facial treatments for glowing skin",
      icon: "‚ú®",
      image: "https://images.unsplash.com/photo-1570172619644-dfd03ed5d881?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    },
    {
      id: 6,
      title: "Bridal Packages",
      description: "Complete bridal beauty services for your special day",
      icon: "üë∞",
      image: "https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=500&q=80"
    }
  ];

  return (
    <section className="py-20 bg-gradient-to-br from-cream-50 to-pearl-100">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Section Header */}
        <div className="text-center mb-16">
          <h2 className="font-playfair text-3xl md:text-4xl font-light text-cream-700 mb-4">
            Our Services
          </h2>
          <p className="font-poppins text-base text-cream-600 max-w-2xl mx-auto font-light">
            Discover our comprehensive range of beauty services designed to make you look and feel your absolute best
          </p>
          <div className="w-16 h-0.5 bg-blush-300 mx-auto mt-6"></div>
        </div>

        {/* Services Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div 
              key={service.id}
              className="group bg-white rounded-2xl shadow-lg hover:shadow-2xl transition-all duration-500 transform hover:-translate-y-2 overflow-hidden"
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              {/* Service Image */}
              <div className="relative h-48 overflow-hidden">
                <img 
                  src={service.image} 
                  alt={service.title}
                  className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent"></div>
                <div className="absolute top-4 right-4 text-3xl bg-white/90 rounded-full w-12 h-12 flex items-center justify-center">
                  {service.icon}
                </div>
              </div>

              {/* Service Content */}
              <div className="p-6">
                <h3 className="font-playfair text-2xl font-semibold text-cream-800 mb-3">
                  {service.title}
                </h3>
                <p className="font-poppins text-cream-600 leading-relaxed">
                  {service.description}
                </p>
                
                {/* Hover Effect Button */}
                <div className="mt-6 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                  <button className="w-full bg-gold-500 hover:bg-gold-600 text-white font-poppins font-medium py-3 px-6 rounded-full transition-colors duration-300">
                    Learn More
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Call to Action */}
        <div className="text-center mt-16">
          <p className="font-poppins text-lg text-cream-700 mb-6">
            Ready to experience luxury beauty services?
          </p>
          <button 
            onClick={() => document.getElementById('appointment').scrollIntoView({ behavior: 'smooth' })}
            className="bg-rose-400 hover:bg-rose-500 text-white font-poppins font-semibold px-8 py-4 rounded-full text-lg transition-all duration-300 transform hover:scale-105 shadow-lg hover:shadow-xl"
          >
            Book Your Service
          </button>
        </div>
      </div>
    </section>
  );
};

export default ServicesSection;
